var myModal=new bootstrap.Modal(document.getElementById("modal-1"));function showModal(){getCookie("hasModalBeenShown")||(myModal.show(),setCookie("hasModalBeenShown","true",1))}function setCookie(e,o,t){const n=new Date;n.setTime(n.getTime()+24*t*60*60*1e3);let i="expires="+n.toUTCString();document.cookie=e+"="+o+";"+i+";path=/"}function getCookie(e){let o=e+"=",t=decodeURIComponent(document.cookie).split(";");for(let e=0;e<t.length;e++){let n=t[e];for(;" "==n.charAt(0);)n=n.substring(1);if(0==n.indexOf(o))return n.substring(o.length,n.length)}return""}window.onload=function(){setTimeout(showModal,1e3)};